import React, {useState} from "react";

export default function Text(props) {
  const [text,setText]=useState("Enter Your Text Here")
  const handleupClick=()=>{
      let newText=text.toUpperCase();
      setText(newText);  
  }  
  const handleloClick=()=>{
    let newText=text.toLowerCase();
    setText(newText);  
} 
  const handleChange=(event)=>{
      setText(event.target.value)
  }
  return (
    <>
    <div>
        <h1>{props.heading}</h1>
        <div className="mb-3">
        <label htmlFor="exampleFormControlTextarea1" className="form-label" >
          Example textarea
        </label>
        <textarea
          className="form-control"
          id="exampleFormControlTextarea1"
          rows="3"
          value={text}
          onChange={handleChange}
        ></textarea>
        </div>
        <button className="btn btn-primary mx-3" onClick={handleupClick}>Uppercase</button>
        <button className="btn btn-primary" onClick={handleloClick}>Lowercase</button>
    </div>
    <div className="container">
      <h1>Text Summary</h1>
      <p>No of words in your text is {text.split(" ").length} and no of characters are {text.length}</p>
    </div>
    </>
  );
}
