import React, { useState } from "react";

export default function Text(props) {
  const [text, setText] = useState("Enter Your Text Here");
  const [count, setcount] = useState(0);
  // let count = 0;
  const handleupClick = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleloClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
  // const setcount=(count)=>{
  //   count=count+1;
  // }
  const handlefontClick = () => {
    let font = () => {
      let val = document.getElementById("exampleFormControlTextarea1");
      val.style.fontStyle = "italic";
    };
    let font2 = () => {
      let val = document.getElementById("exampleFormControlTextarea1");
      val.style.fontStyle = "normal";
    };
    if (count % 2 == 0) {
      font();
    } else {
      font2();
    }
    setcount(count+1);

  };

  const handleChange = (event) => {
    setText(event.target.value);
  };
  return (
    <>
      <div style={{color:props.mode==='light'?'dark':'light'}}>
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <label htmlFor="exampleFormControlTextarea1" className="form-label">
            Example textarea
          </label>
          <textarea
            className="form-control"
            id="exampleFormControlTextarea1"
            rows="3"
            value={text}
            onChange={handleChange}
          ></textarea>
        </div>
        <button className="btn btn-primary mx-0.9" onClick={handleupClick}>
          Uppercase
        </button>
        <button className="btn btn-primary mx-3" onClick={handleloClick}>
          Lowercase
        </button>
        <button className="btn btn-primary" onClick={handlefontClick}>
          Font-Italic
        </button>
      </div >
      <div className="container" style={{color:props.mode==='light'?'dark':'light'}}>
        <h1>Text Summary</h1>
        <p>
          No of words in your text is {text.split(" ").length} and no of
          characters are {text.length}
        </p>
      </div>
    </>
  );
}
