import './App.css';
import Navbar from './components/Navbar';
import Text from './components/Text';
import { ReactPropTypes } from 'react';
import About from './components/About';
import React, { useState } from 'react'


function App() {
  const [darkmode, setdarkmode] = useState('light')
  let togglestate=()=>{
    if(darkmode==='light')
    {
      setdarkmode('dark');
      document.body.style.backgroundColor='black';
    }
    else{
      setdarkmode('light');
      document.body.style.backgroundColor='white';
    }
  }
  let togglegreen=()=>{
    if(darkmode==='light')
    {
      setdarkmode('success');
      document.body.style.backgroundColor='#32CD32';

    }
    else{
      setdarkmode('light');
      document.body.style.backgroundColor='white';
    }
  }
  let togglered=()=>{
    if(darkmode==='light')
    {
      setdarkmode('danger');
      document.body.style.backgroundColor='#cb565d';

    }
    else{
      setdarkmode('light');
      document.body.style.backgroundColor='white';
    }
  }
  let toggleyellow=()=>{
    if(darkmode==='light')
    {
      setdarkmode('warning');
      document.body.style.backgroundColor='#c5c747';

    }
    else{
      setdarkmode('light');
      document.body.style.backgroundColor='white';
    }
  }
  return (
    <>
        <Navbar title="Text-Utils" mode={darkmode} toggle={togglestate} togglegreen={togglegreen} togglered={togglered} toggleyellow={toggleyellow}/>
         <div className="container">
            <Text heading="Enter your Text" mode={darkmode} />
        </div> 
        {/* <About/> */}

    </>
  );
}

export default App;
