import './App.css';
import Navbar from './components/Navbar';
import Text from './components/Text';
import { ReactPropTypes } from 'react';
import About from './components/About';
import React, { useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";


function App() {
  const [darkmode, setdarkmode] = useState('primary')
  let togglestate=()=>{
    if(darkmode==='primary')
    {
      setdarkmode('dark');
      document.body.style.backgroundColor='black';
    }
    else{
      setdarkmode('primary');
      document.body.style.backgroundColor='white';
    }
  }
  let togglegreen=()=>{
    if(darkmode!=='success')
    {
      setdarkmode('success');
      document.body.style.backgroundColor='#32CD32';

    }
    else{
      setdarkmode('primary');
      document.body.style.backgroundColor='white';
    }
  }
  let togglered=()=>{
    if(darkmode==='primary')
    {
      setdarkmode('danger');
      document.body.style.backgroundColor='#cb565d';

    }
    else{
      setdarkmode('primary');
      document.body.style.backgroundColor='white';
    }
  }
  let toggleyellow=()=>{
    if(darkmode==='primary')
    {
      setdarkmode('warning');
      document.body.style.backgroundColor='#999d30';

    }
    else{
      setdarkmode('primary');
      document.body.style.backgroundColor='white';
    }
  }
  return (
    <>
    <Router>
        <Navbar title="Textify" mode={darkmode} toggle={togglestate} togglegreen={togglegreen} togglered={togglered} toggleyellow={toggleyellow}/>

        <Routes>
          <Route path="/about">
          <About/>
          </Route>
          <Route path="/">
          <div className="container">
            <Text heading="Enter your Text" mode={darkmode} />
        </div>
          </Route>
        </Routes>
        </Router>


    </>
  );
}

export default App;
